  > a {
    display: inline-block;
    width: auto;
    color: $header-color;
  }

  &::after {
    $divider: 26px;
    position: absolute;
    top: 50%;
    right: 0;
    width: 0;
    height: $divider;
    margin-top: -($divider / 2) - 2;
    border-left: 2px solid $header-accent;
    content: "";
  }

  &:last-of-type {
    padding-left: $gutter-half;

