components/header/header-branding:
{{#if settings.store_logo.image.data}}
  <a class="header-branding-logo" href="/">
    <img src="{{getImage settings.store_logo.image 'logo'}}" alt="{{settings.store_logo.image.alt}}">
  </a>
{{else}}
  <h1 class="header-branding-text">
    <a href="/">{{settings.store_logo.title}}</a>
  </h1>
{{/if}}

components/footer/footer-branding:
{{#if settings.store_logo.image.data}}
  <a class="footer-branding-logo" href="/">
    <img src="{{getImage settings.store_logo.image 'logo'}}" alt="{{settings.store_logo.image.alt}}">
  </a>
{{else}}
  <h1 class="footer-branding-text">
    <a href="/">{{settings.store_logo.title}}</a>
  </h1>
{{/if}}

components/header/header-navigation-recu:
{{#if children}}
  <ul class="header-navigation-list extended-tier" data-extended-tier>

    {{#each children}}
      <li class="header-navigation-list-item {{#if children}}has-children{{/if}}"
        {{#if children}}data-dropdown-toggle{{/if}}
        {{#if ../../products}}data-products-toggle{{/if}}>
        <a href="{{url}}" data-url="{{url}}">
          <span class="header-navigation-list-item-label">
            {{name}}

            {{#if children}}
              <span class="header-navigation-list-item-arrow">
                <svg><use xlink:href="#icon-chevron-right"></svg>
              </span>

              <span class="header-navigation-list-item-toggle">
                <span class="header-navigation-list-item-toggle-bar"></span>
                <span class="header-navigation-list-item-toggle-bar"></span>
              </span>
            {{/if}}
          </span>
        </a>
        {{> components/header/header-navigation-recursive}}
      </li>
    {{/each}}

    <li class="header-navigation-list-item view-all-link">
      <a href="{{url}}"><span class="header-navigation-list-item-label">{{lang 'header.navigation.view_all' category=name}}</span></a>
    </li>

  </ul>
{{/if}}

components/header/header-mobile-actions:
{{#if settings.account_creation_enabled}}
  <ul class="header-mobile-actions-list">
    {{#if customer}}
      <li class="header-mobile-actions-list-item">
        <a href="{{urls.account.index}}">
