components/pages/gift-certificate/balanc:
{{> core/gift-certificate/navigation page='balance'}}

<div class="giftcard-body">
  <h2 class="giftcard-heading">{{lang 'core.giftcard.balance.heading'}}</h2>
  <p class="giftcard-balance-intro">{{lang 'core.giftcard.balance.subheading'}}</p>

  {{#if gift_certificate}}
    {{#if gift_certificate.error}}
      {{> core/alerts/alert-error gift_certificate.error}}
    {{else}}
      <h2>{{gift_certificate.balance.formatted}}</h2>
      <h3>{{gift_certificate.code}}</h3>
    {{/if}}
  {{/if}}

  <form class="form form-giftcard-balance" id="giftcard-balance" method="get" data-validated-form>
    <input type="hidden" name="action" value="balance">

    {{> core/forms/common/text required=true id="giftcard-code" name="giftcertificatecode" label=(lang 'core.giftcard.balance.code') prefill=(lang 'core.giftcard.balance.code')}}

    <div class="form-actions">
      <input type="submit" class="button button-primary" value="{{lang 'core.giftcard.balance.submit_value' }}">
    </div>
  </form>
</div>

core/gift-certificate/navigation:
{{!
  @param page string
         should be assigned to the active gift card page (purchase, redeeem or balance)
}}

<nav class="giftcard-nav">
  {{#if page '===' 'purchase'}}
    <span class="giftcard-nav-item is-active">
      {{lang 'core.giftcard.purchase.heading'}}
    </span>
  {{else}}
    <a class="giftcard-nav-item" href="{{urls.gift_certificate.purchase}}">
      {{lang 'core.giftcard.purchase.heading'}}
    </a>
  {{/if}}
  {{#if page '===' 'redeem'}}
    <span class="giftcard-nav-item is-active">
      {{lang 'core.giftcard.redeem.heading'}}
    </span>
  {{else}}
    <a class="giftcard-nav-item" href="{{urls.gift_certificate.redeem}}">
      {{lang 'core.giftcard.redeem.heading'}}
    </a>
  {{/if}}
  {{#if page '===' 'balance'}}
    <span class="giftcard-nav-item is-active">
      {{lang 'core.giftcard.balance.heading'}}
    </span>
  {{else}}
    <a class="giftcard-nav-item" href="{{urls.gift_certificate.balance}}">
      {{lang 'core.giftcard.balance.heading'}}
    </a>
  {{/if}}
</nav>
