components/collection/search/search-prod:
{{#if product_results.products}}
  <div class="product-collection-item-list">
    {{#each product_results.products}}
      {{> components/product/product-item overlay=../theme_settings.collection_products_overlay_type}}
    {{/each}}

    {{> components/collection/collection-pagination pagination.product_results}}
  </div>
{{else}}
  <p class="empty-results">
    <span>{{lang 'search.empty_for_product'}} <strong>"{{forms.search.query}}"</strong></span>
  </p>
{{/if}}

components/common/pagination:
{{#if links.length '>' 1}}
  <div class="pagination-container">
    {{#if previous}}
      <div class="pagination-previous">
        <a href="{{previous}}" rel="nofollow" data-faceted-search-facet>{{lang 'core.pagination.previous'}}</a>
      </div>
    {{/if}}

    {{#each links}}
      <div class="pagination-number{{#if ../current '===' number}} current{{/if}}">
        <a href="{{url}}" rel="nofollow" data-faceted-search-facet>{{number}}</a>
      </div>
    {{/each}}

    {{#if next}}
      <div class="pagination-next">
        <a href="{{next}}" rel="nofollow" data-faceted-search-facet>{{lang 'core.pagination.next'}}</a>
      </div>
    {{/if}}
  </div>
{{/if}}
